INSERT INTO "DISTRIBUCION_COMP" VALUES (20, 'Koch Lorber Films', NULL);
INSERT INTO "DISTRIBUCION_COMP" VALUES (21, 'Orion Pictures', 'Orion Pictures Corporation fue una compañía de Hollywood formada por 3 ejecutivos de United Artists, una pequeña empresa con una larga reputación. Orion gano fama por haber producido las películas de Woody Allen, y aunque había ganado varios Oscar por sus filmes, cayó rápidamente en una enorme deuda en los ''90, luego de haber salido de la quiebra, la compañía fundió tras haber sido vendida a Metro-Goldwyn-Mayer. Fue la empresa productora de las películas de Terminator y Robocop.');
INSERT INTO "DISTRIBUCION_COMP" VALUES (22, 'National Film Board of Canada', NULL);
INSERT INTO "DISTRIBUCION_COMP" VALUES (23, 'The Cannon Group Inc', 'The Cannon Group Inc. was a group of companies including Cannon Films which produced a distinctive line of low to medium budget films from 1967 to 1993. The extensive group also owned amongst others, a large international cinema chain and a video film company which invested heavily in the video market, buying the international video rights to several classic film libraries.');
